import { useState } from "react";
import { Link } from "react-router-dom";

import contactLinks from "../assets/contactLinks";

const Contact = () => {
    const [contactHover, setContactHover] = useState(0);
    return (
        <section className="contact">
            {/* <h2>
                Are you working on <em>solving user-focused problems?</em>
                <span>
                    Let's connect if you're looking for an eager beaver to join your team!
                </span>
            </h2> */}
            <h2>
                <span>
                    Are you solving user-focused problems?
                </span>
                Let's connect if you're looking for an eager beaver to join your team!
            </h2>
            <ul>
                {contactLinks.map((link) => {
                    return (
                        <li key={link.id}
                            onMouseEnter={() => setContactHover(link.id)}
                            onMouseLeave={() => setContactHover(0)}>
                            <Link to={link.link}
                                style={
                                    contactHover === link.id ? {
                                        color: `${link.color}`,
                                    } : null}>
                                <div>
                                    <i className={link.i}></i>
                                    <p>:</p>
                                </div>
                                <p>{link.name}</p>
                            </Link>
                        </li>
                    )
                })}
            </ul>
        </section>
    );
};

export default Contact;
