import words from "../assets/words";

const OpeningPage = () => {
    const [randomWord, setRandomWord] = useState(0);

    const getRandomWord = (words) => {
        const randomI = Math.floor(Math.random() * words.length)
        return words[randomI]
    }

    const setWord = setTimeout(setRandomWord(getRandomWord(words)), 1000)



    return (
        <section className="opening-page">
            <p>${randomWord}</p>
            <p>solving</p>
            <div className="user-focused">
                <p>user-</p>
                <p>focused</p>
            </div>
            <p>problems</p>
        </section>
    )
};

export default OpeningPage;
